//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string){
    let paren_groups = paren_string.split(" ");
    let paren_levels = [];
    for(let i = 0; i < paren_groups.length; i++){
        let paren_group = paren_groups[i];
        let paren_level = 0;
        let max_level = 0;
        for(let j = 0; j < paren_group.length; j++){
            if(paren_group[j] == "("){
                paren_level += 1;
                if(paren_level > max_level){
                    max_level = paren_level;
                }
            }
            else if(paren_group[j] == ")"){
                paren_level -= 1;
            }
        }
        paren_levels.push(max_level);
    }
    return paren_levels;
}
