// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome("")
// ""
// >>> make_palindrome("cat")
// "catac"
// >>> make_palindrome("cata")
// "catac"
function make_palindrome(string){
    var palindrome_length = 0;
    var palindrome_end = 0;
    for (var i = 0; i < string.length; i++) {
        var palindrome_length_candidate = get_palindrome_length(string, i);
        if (palindrome_length_candidate > palindrome_length) {
            palindrome_length = palindrome_length_candidate;
            palindrome_end = i;
        }
    }
    var prefix = string.substring(0, palindrome_end + 1 - palindrome_length);
    var suffix = reverse(prefix);
    return string + suffix;
}
//Find the shortest palindrome that begins with a supplied string.
// Algorithm idea is simple:
// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome("")
// ""
// >>> make_palindrome("cat")
// "catac"
// >>> make_palindrome("cata")
// "catac"
function make_palindrome(string){
    if (string.length == 0) {
        return "";
    }
    var palindrome_length = 0;
    for (var i = 0; i < string.length; i++) {
        if (string[i] == string[string.length - 1 - i]) {
            palindrome_length++;
        } else {
            break;
        }
    }
    return string + string.slice(0, string.length - palindrome_length).split("").reverse().join("");
}
//Find the shortest palindrome that begins with a supplied string.
