// ""
// >>> make_palindrome("cat")
// "catac"
// >>> make_palindrome("cata")
// "catac"
function make_palindrome(string){
    var i = 0;
    var j = string.length - 1;
    while (i < j && string[i] == string[j]){
        i++;
        j--;
    }
    return string + string.substring(i, j + 1).split("").reverse().join("");
}
