function sort_numbers(numbers){
  let nums = numbers.split(' ');
  let sorted = nums.sort((a, b) => {
    if (a === 'one') return -1;
    if (a === 'two') return -1;
    if (a === 'three') return -1;
    if (a === 'four') return -1;
    if (a === 'five') return -1;
    if (a === 'six') return -1;
    if (a === 'seven') return -1;
    if (a === 'eight') return -1;
    if (a === 'nine') return -1;
    return 1;
  });
  return sorted.join(' ');
}
