// "hELLO"
function flip_case(string){
    let newString = "";
    for(let i = 0; i < string.length; i++){
        if(string[i] === string[i].toUpperCase()){
            newString += string[i].toLowerCase();
        } else {
            newString += string[i].toUpperCase();
        }
    }
    return newString;
}
