//Return only positive numbers in the array.
// >>> get_positive([-1, 2, -4, 5, 6])
// [2, 5, 6]
// >>> get_positive([5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10])
// [5, 3, 2, 3, 9, 123, 1]
function get_positive(l){
    return l.filter(function(x){return x > 0});
}
//Return only positive numbers in the array.
// >>> get_positive([-1, 2, -4, 5, 6])
// [2, 5, 6]
// >>> get_positive([5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10])
// [5, 3, 2, 3, 9, 123, 1]
function get_positive(l){
    var new_l = [];
    for(var i = 0; i < l.length; i++){
        if(l[i] > 0){
            new_l.push(l[i]);
        }
    }
    return new_l;
}
