//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    var l_prime = l.slice();
    for (var i = 2; i < l.length; i += 3){
        l_prime[i] = l[i];
        for (var j = i + 1; j < l.length; j += 3){
            if (l[j] < l_prime[i]){
                l_prime[i] = l[j];
            }
        }
    }
    return l_prime;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    var l_prime = l.slice();
    var third_indicies = [];
    for(var i = 0; i < l.length; i++){
        if(i % 3 === 0){
            third_indicies.push(i);
        }
    }
    for(var j = 0; j < third_indicies.length; j++){
        var index = third_indicies[j];
        l_prime[index] = l_prime[index].sort();
    }
    return l_prime;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    var l_prime = l.slice();
    var indicies = [];
    for (var i = 0; i < l.length; i++){
        if (i % 3 === 0){
            indicies.push(i);
        }
    }
    for (var i = 0; i < indicies.length; i++){
        var index = indicies[i];
        var sorted_sub_list = l.slice(index, index + 3).sort();
        l_prime.splice(index, 3, sorted_sub_list[0], sorted_sub_list[1], sorted_sub_list[2]);
    }
    return l_prime;
}
