// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
    var l_prime = l.slice(0);
    var third_indicies = [];
    for(var i = 0; i < l.length; i++){
        if(i % 3 === 0){
            third_indicies.push(i);
        }
    }
    for(var i = 0; i < third_indicies.length; i++){
        var index = third_indicies[i];
        var value = l[index];
        l_prime.splice(index, 1);
        l_prime.sort(function(a, b){return a - b;});
        l_prime.splice(index, 0, value);
    }
    return l_prime;
