//This function takes an array l and returns an array l' such that
// l' is identical to l in the odd indicies, while its values at the even indicies are equal
// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l){
    var l_prime = l.slice();
    var even_indicies = [];
    for(var i = 0; i < l.length; i++){
        if(i % 2 == 0){
            even_indicies.push(i);
        }
    }
    even_indicies.forEach(function(index){
        l_prime[index] = l[index];
    });
    even_indicies.forEach(function(index){
        var temp = l_prime[index];
        var j = index;
        while(j > 0 && l_prime[j - 1] > temp){
            l_prime[j] = l_prime[j - 1];
            j--;
        }
        l_prime[j] = temp;
    });
    return l_prime;
}
