// n cars are driving left to right;  simultaneously, a different set of n cars
// are driving right to left.   The two sets of cars start out being very far from
// each other.  All cars move in the same speed.  Two cars are said to collide
// when a car that's moving left to right hits a car that's moving right to left.
// However, the cars are infinitely sturdy and strong; as a result, they continue moving
// in their trajectory as if they did not collide.
// This function outputs the number of such collisions.
function car_race_collision(n){
  var left_to_right = [];
  var right_to_left = [];
  var collisions = 0;
  for(var i = 0; i < n; i++){
    left_to_right.push(i);
    right_to_left.push(i);
  }
  for(var i = 0; i < n; i++){
    for(var j = 0; j < n; j++){
      if(left_to_right[i] === right_to_left[j]){
        collisions++;
      }
    }
  }
  return collisions;
}
//Imagine a road that's a perfectly straight infinitely long line.
// n cars are driving left to right;  simultaneously, a different set of n cars
// are driving right to left.   The two sets of cars start out being very far from
// each other.  All cars move in the same speed.  Two cars are said to collide
// when a car that's moving left to right hits a car that's moving right to left.
// However, the cars are infinitely sturdy and strong; as a result, they continue moving
// in their trajectory as if they did not collide.
// This function outputs the number of such collisions.
function car_race_collision(n){
    //if there are no cars, there are no collisions
    if(n === 0){
        return 0;
    }
    //if there is only one car, there is no collision
    if(n === 1){
        return 0;
    }
    //if there are two cars, there is one collision
    if(n === 2){
        return 1;
    }
    //if there are more than two cars, we can use the following recursive formula:
    //if n cars are moving left to right, and n cars are moving right to left,
    //there are n-1 collisions.  If we remove one car from each set, we have
    //n-2 cars moving left to right, and n-2 cars moving right to left.  There
    //are (n-2)-1 collisions.  Therefore, there are n-1 + (n-2)-1 = n-1 + n-3 = 2n-4
    //collisions.
    return 2*n - 4 + car_race_collision(n-2);
}
