//pairs_sum_to_zero takes an array of integers as an input.
// it returns true if there are two distinct elements in the array that
// sum to zero, and false otherwise.
// >>> pairs_sum_to_zero([1, 3, 5, 0])
// false
// >>> pairs_sum_to_zero([1, 3, -2, 1])
// false
// >>> pairs_sum_to_zero([1, 2, 3, 7])
// false
// >>> pairs_sum_to_zero([2, 4, -5, 3, 5, 7])
// true
// >>> pairs_sum_to_zero([1])
// false
function pairs_sum_to_zero(l){
    var i = 0;
    var j = l.length - 1;
    while (i < j){
        if (l[i] + l[j] === 0){
            return true;
        }
        if (l[i] + l[j] < 0){
            i++;
        } else {
            j--;
        }
    }
    return false;
}
//pairs_sum_to_zero takes an array of integers as an input.
// it returns true if there are two distinct elements in the array that
// sum to zero, and false otherwise.
// >>> pairs_sum_to_zero([1, 3, 5, 0])
// false
// >>> pairs_sum_to_zero([1, 3, -2, 1])
// false
// >>> pairs_sum_to_zero([1, 2, 3, 7])
// false
// >>> pairs_sum_to_zero([2, 4, -5, 3, 5, 7])
// true
// >>> pairs_sum_to_zero([1])
// false
function pairs_sum_to_zero(l){
    // if the array is empty, return false
    if (l.length === 0) {
        return false;
    }
    // if the array has only one element, return false
    if (l.length === 1) {
        return false;
    }
    // if the array has two elements, return true if they sum to zero
    if (l.length === 2) {
        return l[0] + l[1] === 0;
    }
    // if the array has more than two elements, return true if there are two distinct elements that sum to zero
    for (let i = 0; i < l.length; i++) {
        for (let j = i + 1; j < l.length; j++) {
            if (l[i] + l[j] === 0) {
                return true;
            }
        }
    }
    return false;
}
