// >>> modp(3, 11)
// 8
// >>> modp(100, 101)
// 1
function modp(n, p){
    if (n == 0) return 1;
    var res = 1;
    while (n > 0){
        if (n & 1){
            res = (res * 2) % p;
        }
        n >>= 1;
    }
    return res;
}
