  for(let i = 0; i < l1.length; i++){
    for(let j = 0; j < l2.length; j++){
      if(l1[i] === l2[j]){
        l3.push(l1[i]);
      }
    }
  }
  return l3.sort().filter((item, index, array) => array.indexOf(item) === index);
}
//Return sorted unique common elements for two arrays.
// >>> common([1, 4, 3, 34, 653, 2, 5], [5, 7, 1, 5, 9, 653, 121])
// [1, 5, 653]
// >>> common([5, 3, 2, 8], [3, 2])
// [2, 3]
function common(l1, l2){
    var l3 = [];
    var l4 = [];
    var l5 = [];
    for(var i = 0; i < l1.length; i++){
        if(l1[i] > 0){
            l3.push(l1[i]);
        }
    }
    for(var j = 0; j < l2.length; j++){
        if(l2[j] > 0){
            l4.push(l2[j]);
        }
    }
    l3.sort();
    l4.sort();
    for(var k = 0; k < l3.length; k++){
        for(var m = 0; m < l4.length; m++){
            if(l3[k] == l4[m]){
                l5.push(l3[k]);
            }
        }
    }
    return l5;
}
//Return sorted unique common elements for two arrays.
// >>> common([1, 4, 3, 34, 653, 2, 5], [5, 7, 1, 5, 9, 653, 121])
// [1, 5, 653]
// >>> common([5, 3, 2, 8], [3, 2])
// [2, 3]
function common(l1, l2){
    let result = [];
    let hash = {};
    for(let i = 0; i < l1.length; i++){
        hash[l1[i]] = true;
    }
    for(let i = 0; i < l2.length; i++){
        if(hash[l2[i]] === true){
            result.push(l2[i]);
            delete hash[l2[i]];
        }
    }
    return result.sort((a, b) => a - b);
}
