// >>> correct_bracketing("()")
// true
// >>> correct_bracketing("(()())")
// true
// >>> correct_bracketing(")(()")
// false
function correct_bracketing(brackets){
    let stack = [];
    for(let i = 0; i < brackets.length; i++){
        if(brackets[i] === "("){
            stack.push("(");
        }
        else{
            if(stack.length === 0){
                return false;
            }
            else{
                stack.pop();
            }
        }
    }
    return stack.length === 0;
}
