    let count = 0;
    let i = 0;
    let j = arr.length - 1;
    while(i < j){
        if(arr[i] !== arr[j]){
            count++;
            if(arr[i+1] === arr[j]){
                arr[i] = arr[j];
            } else {
                arr[j] = arr[i];
            }
        }
        i++;
        j--;
    }
    return count;
