    let result = "";
    let vowels = "aeiou";
    let vowelsIndex = 0;
    let vowelsIndexes = [];
    let vowelsIndexesLength = 0;
    let vowelsIndexesLengthIndex = 0;
    for(let i = 0; i < message.length; i++){
        if(vowels.includes(message[i])){
            vowelsIndexes[vowelsIndexesLength] = i;
            vowelsIndexesLength++;
        }
    }
    for(let i = 0; i < message.length; i++){
        if(vowels.includes(message[i])){
            result += message[i].toUpperCase();
        } else {
            result += message[i].toLowerCase();
        }
        if(vowelsIndexesLengthIndex < vowelsIndexesLength){
            if(i === vowelsIndexes[vowelsIndexesLengthIndex]){
                vowelsIndexesLengthIndex++;
                vowelsIndex++;
                if(vowelsIndex === 5){
                    vowelsIndex = 0;
                }
                result += vowels[vowelsIndex];
            }
        }
    }
    return result;
}
