    let lowerCase = true;
    let upperCase = true;
    for(let key in dict){
        if(key.toLowerCase() !== key){
            lowerCase = false;
        }
        if(key.toUpperCase() !== key){
            upperCase = false;
        }
    }
    return lowerCase || upperCase;
}
