// For similar number of ones, sort based on decimal value.
// It must be implemented like this:
// >>> sort_array([1, 5, 2, 3, 4])
// [1, 2, 3, 4, 5]
// >>> sort_array([-2, -3, -4, -5, -6])
// [-6, -5, -4, -3, -2]
// >>> sort_array([1, 0, 2, 3, 4])
// [0, 1, 2, 3, 4]
function sort_array(arr){
  return arr.sort((a, b) => {
    let aBin = a.toString(2);
    let bBin = b.toString(2);
    if (aBin.split('1').length === bBin.split('1').length) {
      return a - b;
    }
    return aBin.split('1').length - bBin.split('1').length;
  });
}
//Given a string s and a natural number n, you have been tasked to implement 
// a function that returns an array of all words from string s that contain exactly 
// n consonants, in order these words appear in the string s.
// If the string s is empty then the function should return an empty array.
// Note: you may assume the input string contains only letters and spaces.
// Examples:
// >>> select_words("Mary had a little lamb", 4)
// ["little"]
// >>> select_words("Mary had a little lamb", 3)
// ["Mary", "lamb"]
// >>> select_words("simple white space", 2)
// []
// >>> select_words("Hello world", 4)
// ["world"]
// >>> select_words("Uncle sam", 3)
// ["Uncle"]
function select_words(s, n){
    let words = s.split(' ');
    let result = [];
    for(let i = 0; i < words.length; i++){
        let count = 0;
        for(let j = 0; j < words[i].length; j++){
            if(words[i][j] != 'a' && words[i][j] != 'e' && words[i][j] != 'i' && words[i][j] != 'o' && words[i][j] != 'u'){
                count++;
            }
        }
        if(count == n){
            result.push(words[i]);
        }
    }
    return result;
}
