// 1. The length of the array will be in the range of [1, 1000].
// 2. The elements in the array will be in the range of [-1000, 1000].
// 3. 0 <= k <= len(arr)
function maximum(arr, k){
    let result = [];
    for(let i = 0; i < k; i++){
        result.push(arr[i]);
    }
    let j = 0;
    for(let i = k; i < arr.length; i++){
        if(arr[i] > result[j]){
            result[j] = arr[i];
            result.sort((a, b) => b - a);
            j++;
        }
    }
    return result;
}
