// [1, 5]
function get_odd_collatz(n){
    let result = [];
    let current = n;
    while(current > 1){
        if(current % 2 == 0){
            current = current / 2;
        }else{
            current = 3 * current + 1;
        }
        if(current % 2 == 1){
            result.push(current);
        }
    }
    return result.sort((a, b) => a - b);
}
