// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){
  let openBrackets = 0;
  let closedBrackets = 0;
  for (let i = 0; i < string.length; i++) {
    if (string[i] === "[") {
      openBrackets++;
    } else if (string[i] === "]") {
      closedBrackets++;
    }
    if (closedBrackets > openBrackets) {
      return false;
    }
  }
  return openBrackets !== closedBrackets;
}
