// false
// >>> check_if_last_char_is_a_letter("")
// false
function check_if_last_char_is_a_letter(txt){
  if(txt.length === 0) return false;
  let lastChar = txt.slice(-1);
  let lastCharIsLetter = lastChar.match(/[a-zA-Z]/);
  let lastCharIsSpace = lastChar.match(/\s/);
  if(lastCharIsLetter && !lastCharIsSpace) return true;
  return false;
}
