//Write a function which sorts the given array of integers
// in ascending order according to the sum of their digits.
// Note: if there are several items with similar sum of their digits,
// order them based on their index in original array.
// For example:
// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){
    let sum = [];
    let newArr = [];
    for(let i = 0; i < nums.length; i++){
        let num = nums[i].toString().split('');
        let sumNum = 0;
        for(let j = 0; j < num.length; j++){
            sumNum += parseInt(num[j]);
        }
        sum.push(sumNum);
    }
    for(let i = 0; i < nums.length; i++){
        let min = Math.min(...sum);
        let index = sum.indexOf(min);
        newArr.push(nums[index]);
        sum.splice(index, 1);
        nums.splice(index, 1);
    }
    return newArr;
}
