// 2
function specialFilter(nums){
    let count = 0;
    for(let i = 0; i < nums.length; i++){
        if(nums[i] > 10 && (nums[i] % 10 === 1 || nums[i] % 10 === 3 || nums[i] % 10 === 5 || nums[i] % 10 === 7 || nums[i] % 10 === 9) && (nums[i] % 100 < 10 || nums[i] % 100 > 90)){
            count++;
        }
    }
    return count;
}
//Write a function that takes an array of numbers as input and returns 
// the number of elements in the array that are greater than 10 and both 
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// >>> specialFilter([15, -73, 14, -15])
// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums){
    let count = 0;
    for (let i = 0; i < nums.length; i++){
        let num = nums[i];
        let firstDigit = Math.abs(num) % 10;
        let lastDigit = Math.abs(num) % 100;
        lastDigit = lastDigit - (lastDigit % 10);
        if (num > 10 && (firstDigit % 2 === 1 || lastDigit % 2 === 1)){
            count++;
        }
    }
    return count;
}
//Write a function that takes an array of numbers as input and returns 
// the number of elements in the array that are greater than 10 and both 
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// >>> specialFilter([15, -73, 14, -15])
// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums){
    let count = 0;
    for (let i = 0; i < nums.length; i++){
        if (nums[i] > 10){
            let num = nums[i].toString();
            if (num[0] % 2 !== 0 && num[num.length - 1] % 2 !== 0){
                count++;
            }
        }
    }
    return count;
}
