//A simple program which should return the value of x if n is 
// a prime number and should return the value of y otherwise.
// Examples:
// >>> x_or_y(7, 34, 12)
// 34
// >>> x_or_y(15, 8, 5)
// 5
function x_or_y(n, x, y){
    if(n == 2){
        return x;
    }
    if(n % 2 == 0){
        return y;
    }
    for(let i = 3; i < n; i+=2){
        if(n % i == 0){
            return y;
        }
    }
    return x;
}
//A simple program which should return the value of x if n is 
// a prime number and should return the value of y otherwise.
// Examples:
// >>> x_or_y(7, 34, 12)
// 34
// >>> x_or_y(15, 8, 5)
// 5
function x_or_y(n, x, y){
    if (n == 2) {
        return x;
    }
    if (n % 2 == 0) {
        return y;
    }
    for (let i = 3; i < Math.sqrt(n); i += 2) {
        if (n % i == 0) {
            return y;
        }
    }
    return x;
}
