// Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
// separate those group into separate strings and return the vector of those.
// Separate groups are balanced (each open brace is properly closed) and not nested within each other
// Ignore any spaces in the input string.
// >>> separate_paren_groups(("( ) (( )) (( )( ))"))
// (std::vector<std::string>({(std::string)"()", (std::string)"(())", (std::string)"(()())"}))
std::vector<std::string> separate_paren_groups(std::string paren_string) {std::vector<std::string> result;
    std::string temp;
    int open_braces = 0;
    for (char c : paren_string) {
        if (c == '(') {
            open_braces++;
            temp.push_back(c);
        } else if (c == ')') {
            open_braces--;
            temp.push_back(c);
            if (open_braces == 0) {
                result.push_back(temp);
                temp.clear();
            }
        } else if (c != ' ') {
            temp.push_back(c);
        }
    }
    return result;

