// Find the shortest palindrome that begins with a supplied string.
// Algorithm idea is simple:
// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome((""))
// ("")
// >>> make_palindrome(("cat"))
// ("catac")
// >>> make_palindrome(("cata"))
// ("catac")
std::string make_palindrome(std::string string) {
    int n = string.size();
    std::vector<bool> table(n, false);
    int center = 0;
    for (int i = 0; i < n; ++i) {
        if (i < center + table[center - i]) {
            table[i] = table[center - i];
        }
        while (i - table[i] >= 0 && i + table[i] < n && string[i - table[i]] == string[i + table[i]]) {
            ++table[i];
        }
        if (i + table[i] > center + table[center]) {
            center = i;
        }
    }
    std::string add = string.substr(0, n - table[n - 1]);
    std::reverse(add.begin(), add.end());
    return string + add;

