// brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing(("<"))
// (false)
// >>> correct_bracketing(("<>"))
// (true)
// >>> correct_bracketing(("<<><>>"))
// (true)
// >>> correct_bracketing(("><<>"))
// (false)
bool correct_bracketing(std::string brackets) {
    std::stack<char> stack;
    for (char c : brackets) {
        if (c == '<') {
            stack.push(c);
        } else {
            if (stack.empty()) {
                return false;
            }
            stack.pop();
        }
    }
    return stack.empty();

