#include<assert.h>
#include<bits/stdc++.h>
// Return true is vector elements are monotonically increasing or decreasing.
// >>> monotonic((std::vector<long>({(long)1, (long)2, (long)4, (long)20})))
// (true)
// >>> monotonic((std::vector<long>({(long)1, (long)20, (long)4, (long)10})))
// (false)
// >>> monotonic((std::vector<long>({(long)4, (long)1, (long)0, (long)-10})))
// (true)
bool monotonic(std::vector<long> l) {
    if (l.size() <= 1)
        return true;
    int dir = l[1] - l[0];
    for (size_t i = 2; i < l.size(); i++) {
        if ((l[i] - l[i - 1]) * dir < 0)
            return false;
        dir = l[i] - l[i - 1];
    }
    return true;

#include<assert.h>
#include<bits/stdc++.h>
// Return true is vector elements are monotonically increasing or decreasing.
// >>> monotonic((std::vector<long>({(long)1, (long)2, (long)4, (long)20})))
// (true)
// >>> monotonic((std::vector<long>({(long)1, (long)20, (long)4, (long)10})))
// (false)
// >>> monotonic((std::vector<long>({(long)4, (long)1, (long)0, (long)-10})))
// (true)
bool monotonic(std::vector<long> l) {
    if (l.size() <= 1) {
        return true;
    }
    bool increasing = l[0] <= l[1];
    bool decreasing = l[0] >= l[1];
    for (size_t i = 2; i < l.size(); i++) {
        if (increasing && l[i - 1] > l[i]) {
            return false;
        }
        if (decreasing && l[i - 1] < l[i]) {
            return false;
        }
    }
    return true;

