// Return the largest prime factor of n. Assume n > 1 and is not a prime.
// >>> largest_prime_factor((13195))
// (29)
// >>> largest_prime_factor((2048))
// (2)
long largest_prime_factor(long n) {
    long max_prime = -1;
    while (n % 2 == 0) {
        max_prime = 2;
        n >>= 1; // equivalent to n /= 2
    }
    for (long i = 3; i * i <= n; i += 2) {
        while (n % i == 0) {
            max_prime = i;
            n = n / i;
        }
    }
    if (n > 2)
        max_prime = n;
    return max_prime;

