// (1)
// >>> smallest_change((std::vector<long>({(long)1, (long)2, (long)3, (long)2, (long)1})))
// (0)
long smallest_change(std::vector<long> arr) {
    long n = arr.size();
    long changes = 0;
    std::vector<long> dp(n, 0);
    for (long i = 0; i < n; i++) {
        dp[i] = 1;
    }
    for (long l = 2; l <= n; l++) {
        for (long i = 0; i <= n - l; i++) {
            long j = i + l - 1;
            if (arr[i] == arr[j]) {
                dp[i] = dp[i] + dp[i + 1];
            } else {
                dp[i] = std::min(dp[i], dp[i + 1]);
            }
        }
    }
    changes = dp[0];
    return changes;

