#include <algorithm>
#include <string>
#include <sstream>

std::string anti_shuffle(std::string s) {
    std::istringstream iss(s);
    std::string word, res;

    while (iss >> word) {
        std::sort(word.begin(), word.end());
        res += word + " ";
    }

    // remove the last unnecessary space
    if (!res.empty()) {
        res.pop_back();
    }

