// Implement a function that takes an non-negative integer and returns a vector of the first n
// integers that are prime numbers and less than n.
// for example:
// >>> count_up_to((5))
// (std::vector<long>({(long)2, (long)3}))
// >>> count_up_to((11))
// (std::vector<long>({(long)2, (long)3, (long)5, (long)7}))
// >>> count_up_to((0))
// (std::vector<long>())
// >>> count_up_to((20))
// (std::vector<long>({(long)2, (long)3, (long)5, (long)7, (long)11, (long)13, (long)17, (long)19}))
// >>> count_up_to((1))
// (std::vector<long>())
// >>> count_up_to((18))
// (std::vector<long>({(long)2, (long)3, (long)5, (long)7, (long)11, (long)13, (long)17}))
std::vector<long> count_up_to(long n) {
    std::vector<long> primes;
    if (n < 2)
        return primes;
    for (long i = 2; i < n; ++i) {
        bool isPrime = true;
        for (long j = 2; j * j <= i; ++j) {
            if (i % j == 0) {
                isPrime = false;
                break;
            }
        }
        if (isPrime)
            primes.push_back(i);
    }
    return primes;

