    // Create a predicate to check if a number has even digit
    auto has_even_digit = [](long num) {
        while (num > 0) {
            if ((num % 10) % 2 == 0) {
                return true;
            }
            num /= 10;
        }
        return false;
    };

    // Remove numbers with even digit
    x.erase(std::remove_if(x.begin(), x.end(), has_even_digit), x.end());

    // Sort the remaining numbers
    std::sort(x.begin(), x.end());

    return x;

