            for (long j = 1; j <= i; ++j) {
                factorial *= j;
            }
            result[i - 1] = factorial;
        } else {
            long sum = 0;
            for (long j = 1; j <= i; ++j) {
                sum += j;
            }
            result[i - 1] = sum;
        }
    }
    return result;

