        long sum = 0;
        bool is_negative = false;
        if (num < 0) {
            is_negative = true;
            num = -num;
        }
        while (num > 0) {
            sum += num % 10;
            num /= 10;
        }
        if (is_negative) {
            sum = -sum;
        }
        if (sum > 0) {
            count++;
        }
    }
    return count;

