#include <string>
#include <unordered_set>
#include <algorithm>
#include <tuple>

std::tuple<std::string, bool> reverse_delete(std::string s, std::string c) {
    // Create a set of characters to delete
    std::unordered_set<char> to_delete;
    for (char ch : c) {
        to_delete.insert(ch);
    }

    // Delete characters from string
    s.erase(std::remove_if(s.begin(), s.end(), [&](char ch) { return to_delete.count(ch); }), s.end());

    // Check if string is palindrome
    bool is_palindrome = s == std::string(s.rbegin(), s.rend());

    return std::make_tuple(s, is_palindrome);

