import heapq

def minPath(grid, k):
    n = len(grid)
    visited = set()
    queue = [(grid[0][0], 0, 0, [grid[0][0]])]
    while queue:
        val, x, y, path = heapq.heappop(queue)
        if len(path) == k:
            return path
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < n and (nx, ny) not in visited:
                visited.add((nx, ny))
                new_val = grid[nx][ny]
                new_path = path[:-1] + [new_val]

