from collections import deque

def minPath(grid, k):
    n = len(grid)
    visited = set()
    q = deque([(grid[0][0], 0, 0, [grid[0][0]])])
    while q:
        val, r, c, path = q.popleft()
        if len(path) == k:
            return path
        for dr, dc in ((-1, 0), (1, 0), (0, -1), (0, 1)):
            nr, nc = r + dr, c + dc
            if 0 <= nr < n and 0 <= nc < n and (nr, nc) not in visited:
                visited.add((nr, nc))
                q.append((grid[nr][nc], nr, nc, path + [grid[nr][nc]]))
    return []
