// and a[i] + a[j] + a[k] is a multiple of 3.
// Example :
// >>> get_max_triples((5))
// (1)
// Explanation: 
// a = [1, 3, 7, 13, 21]
// The only valid triple is (1, 7, 13).
long get_max_triples(long n) {
    long count = 0;
    vector<long> a(n);
    for (long i = 1; i <= n; i++) {
        a[i - 1] = i * i - i + 1;
    }
    for (long i = 0; i < n; i++) {
        for (long j = i + 1; j < n; j++) {
            for (long k = j + 1; k < n; k++) {
                if ((a[i] + a[j] + a[k]) % 3 == 0) {
                    count++;
                }
            }
        }
    }
    return count;

