// ("cdxxvi")
std::string int_to_mini_roman(long number) {
    std::string roman = "";
    while (number >= 1000) {
        number -= 1000;
        roman += "m";
    }
    while (number >= 900) {
        number -= 900;
        roman += "cm";
    }
    while (number >= 500) {
        number -= 500;
        roman += "d";
    }
    while (number >= 400) {
        number -= 400;
        roman += "cd";
    }
    while (number >= 100) {
        number -= 100;
        roman += "c";
    }
    while (number >= 90) {
        number -= 90;
        roman += "xc";
    }
    while (number >= 50) {
        number -= 50;
        roman += "l";
    }
    while (number >= 40) {
        number -= 40;
        roman += "xl";
    }
    while (number >= 10) {
        number -= 10;
        roman += "x";
    }
    while (number >= 9) {
        number -= 9;
        roman += "ix";
    }
    while (number >= 5) {
        number -= 5;
        roman += "v";
    }
    while (number >= 4) {
        number -= 4;
        roman += "iv";
    }
    while (number >= 1) {
        number -= 1;
        roman += "i";
    }
    return roman;

