// Write a function that accepts a vector of strings.
// The vector contains different words. Return the word with maximum number
// of unique characters. If multiple strings have maximum number of unique
// characters, return the one which comes first in lexicographical order.
// >>> find_max((std::vector<std::string>({(std::string)"name", (std::string)"of", (std::string)"string"})))
// ("string")
// >>> find_max((std::vector<std::string>({(std::string)"name", (std::string)"enam", (std::string)"game"})))
// ("enam")
// >>> find_max((std::vector<std::string>({(std::string)"aaaaaaa", (std::string)"bb", (std::string)"cc"})))
// ("aaaaaaa")
std::string find_max(std::vector<std::string> words) {
    std::string max_word = "";
    int max_unique_chars = 0;
    for (std::string word : words) {
        std::set<char> unique_chars(word.begin(), word.end());
        if (unique_chars.size() > max_unique_chars) {
            max_unique_chars = unique_chars.size();
            max_word = word;
        } else if (unique_chars.size() == max_unique_chars) {
            max_word = std::min(max_word, word);
        }
    }
    return max_word;

