// Perform binary XOR on these inputs and return result also as a string.
// >>> string_xor(("010"), ("110"))
// ("100")
std::string string_xor(std::string a, std::string b) {
    std::string result;
    int n = a.size();
    for(int i=0; i<n; i++) {
        if(a[i] == b[i]) {
            result += "0";
        } else {
            result += "1";
        }
    }
    return result;

