// Out of vector of strings, return the longest one. Return the first one in case of multiple
// strings of the same length. Return None in case the input vector is empty.
// >>> longest((std::vector<std::string>()))
// std::nullopt
// >>> longest((std::vector<std::string>({(std::string)"a", (std::string)"b", (std::string)"c"})))
// "a"
// >>> longest((std::vector<std::string>({(std::string)"a", (std::string)"bb", (std::string)"ccc"})))
// "ccc"
std::optional<std::string> longest(std::vector<std::string> strings) {
    if (strings.empty()) {
        return std::nullopt;
    }
    std::string longest_str = strings[0];
    for (auto& str : strings) {
        if (str.length() > longest_str.length()) {
            longest_str = str;
        }
    }
    return longest_str;

