// Checks if given string is a palindrome
// >>> is_palindrome((""))
// (true)
// >>> is_palindrome(("aba"))
// (true)
// >>> is_palindrome(("aaaaa"))
// (true)
// >>> is_palindrome(("zbcd"))
// (false)
bool is_palindrome(std::string text) {
    int n = text.size();
    for (int i = 0; i < n/2; i++) {
        if (text[i] != text[n-i-1]) {
            return false;
        }
    }
    return true;

