        } else {
            result.push_back(max_val);
            lst.erase(std::remove(lst.begin(), lst.end(), max_val), lst.end());
        }
        min_val = *std::min_element(lst.begin(), lst.end());
        max_val = *std::max_element(lst.begin(), lst.end());
    }
