// (1)
// >>> smallest_change((std::vector<long>({(long)1, (long)2, (long)3, (long)2, (long)1})))
// (0)
long smallest_change(std::vector<long> arr) {
    int n = arr.size();
    int left = 0, right = n - 1;
    int count = 0;
    while (left < right) {
        if (arr[left] != arr[right]) {
            count++;
            if (arr[left + 1] == arr[right]) {
                arr[right] = arr[left];
            } else if (arr[left] == arr[right - 1]) {
                arr[left] = arr[right];
            } else {
                arr[left] = arr[right];
            }
        }
        left++;
        right--;
    }
    return count;

