// For example:
// >>> is_happy(("a"))
// (false)
// >>> is_happy(("aa"))
// (false)
// >>> is_happy(("abcd"))
// (true)
// >>> is_happy(("aabb"))
// (false)
// >>> is_happy(("adb"))
// (true)
// >>> is_happy(("xyy"))
// (false)
bool is_happy(std::string s) {
    if(s.size() < 3) return false;
    for(int i=0; i<s.size()-2; i++){
        if(s[i] == s[i+1] || s[i+1] == s[i+2] || s[i+2] == s[i]) return false;
    }
    return true;

