// Given a vector of positive integers x. return a sorted vector of all 
// elements that hasn't any even digit.
// Note: Returned vector should be sorted in increasing order.
// For example:
// >>> unique_digits((std::vector<long>({(long)15, (long)33, (long)1422, (long)1})))
// (std::vector<long>({(long)1, (long)15, (long)33}))
// >>> unique_digits((std::vector<long>({(long)152, (long)323, (long)1422, (long)10})))
// (std::vector<long>())
std::vector<long> unique_digits(std::vector<long> x) {
    std::vector<long> res;
    for(long i : x) {
        bool has_even_digit = false;
        while(i > 0) {
            if(i % 2 == 0) {
                has_even_digit = true;
                break;
            }
            i /= 10;
        }
        if(!has_even_digit) {
            res.push_back(i);
        }
    }
    std::sort(res.begin(), res.end());
    return res;

