// Write a function count_nums which takes a vector of integers and returns
// the number of elements which has a sum of digits > 0.
// If a number is negative, then its first signed digit will be negative:
// e.g. -123 has signed digits -1, 2, and 3.
// >>> count_nums((std::vector<long>()))
// (0)
// >>> count_nums((std::vector<long>({(long)-1, (long)11, (long)-11})))
// (1)
// >>> count_nums((std::vector<long>({(long)1, (long)1, (long)2})))
// (3)
long count_nums(std::vector<long> arr) {
    long count = 0;
    for (long i : arr) {
        long sum = 0;
        if (i < 0) {
            i = -i;
            while (i > 0) {
                sum += i % 10;
                i /= 10;
            }
        } else {
            while (i > 0) {
                sum += i % 10;
                i /= 10;
            }
        }
        if (sum > 0) {
            count++;
        }
    }
    return count;

